mapper.select:
select

  distinct


from t_par_client_level temp_par_client_level

  


  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}

select 

from t_par_client_level temp_par_client_level
where temp_par_client_level.client_level_id = #{clientLevelId,jdbcType=VARCHAR}

