  #{sequenced,jdbcType=INTEGER}, #{enabled,jdbcType=CHAR}, #{creater,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updater,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
  )
