  distinct


from t_par_payment_type temp_par_payment_type

  


  order by ${orderByClause}


  limit ${limitStart} , ${limitEnd}

select 

from t_par_payment_type temp_par_payment_type
where temp_par_payment_type.payment_type_id = #{paymentTypeId,jdbcType=VARCHAR}

